import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

function CreateTeamRoster() {
  const [teams, setTeams] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState('');
  const [selectedUsers, setSelectedUsers] = useState([]);

  useEffect(()=>{
	axios.get('/getUsers').then((res)=>setUsers(res.data))
	axios.get('/getTeams').then((res)=>setTeams(res.data))
}, []);



  // Function to handle adding members to the team
  const handleAddMembers = () => {
    // API call to add selectedUsers to selectedTeam
    // Example: axios.post('http://localhost:9000/addMembers', { teamId: selectedTeam, members: selectedUsers })
    console.log(`Adding members ${selectedUsers} to team ${selectedTeam}`);
  };

  return (
    <div>
      <h2>Create Team Roster</h2>
      <label>Select Team:</label>
      <Select
        value={selectedTeam}
        onChange={setSelectedTeam}
        options={teams.map(team => ({ label: team.team_name, value: team._id }))}
      />
      <label>Select Members:</label>
      <Select
        isMulti
        value={selectedUsers}
        onChange={setSelectedUsers}
        options={users.map(user => ({ label: `${user.f_Name} ${user.l_Name}`, value: user._id }))}
      />
      <button onClick={handleAddMembers}>Add Members</button>
    </div>
  );
}

export default CreateTeamRoster;
