import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
/*
For the Signup page, this also needed to take in the input
the user inputs so need a handeler, space for first and last name 
user and password and a submit button and submit button to the thank you page
*/
function SignUp() {

    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const signupContainerStyle = {
      textAlign: 'center',
      color: 'blue', 
      marginTop: '50px', 
    };

    const signupStyle = {
      color: 'green',
      margin: '50px',
    };

    const header={
      color: 'pink',
      textAlign: 'center',
    };

    const handleSignUp = (event) => {
      event.preventDefault();
      axios.post('http://localhost:9000/signup', { firstName, lastName, username, password })
        .then((res) => {
          alert('Signup was Successful');
          //to go back to the main page where user gets to pick login/ sign up again
          window.location.href = '/';
        })
        .catch((err) => alert('Error in Signing Up'));
    };

    return (
      <div style={signupContainerStyle}>
        <h1 style={header}>Sign Up</h1>
        <label>
          First name:
          <input id="f_Name" name="firstName" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
        </label>
        <label>
          Last name:
          <input id="l_Name" name="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} />
        </label>
        <label>
          User name:
          <input id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <label>
          Password:
          <input id="password" name="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <Link to="/ThankYou">
          <button  style= {signupStyle} onClick={handleSignUp}>Submit</button>
        </Link>

      <Link to="/">
        <button style={signupStyle}>Go back</button>
      </Link>
      </div>
    );
  }
  export default SignUp;
