import React, { useState, useEffect } from 'react';
import axios from 'axios';


/*
Create User Story: Create a page to add user stories to a project.
• Fetch a list of all created projects.
• Pick a project from a list.
• Add a descrip4on of the user story.
• Assign a priority to the user story (default:0)
*/
function CreateUserStory() {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState('');
  const [description, setDescription] = useState('');
  const [priority, setPriority] = useState(0);

  useEffect(() => {
    // Fetch list of all created projects
    axios.get('http://localhost:9000/UserStory')
      .then(response => {
        setProjects(response.data);
      })
      .catch(error => {
        console.error('Error creating user story', error);
      });
  }, []);

  // Function to handle submitting the user story
  const handleSubmit = (event) => {
    event.preventDefault()
    axios.post('http://localhost:9000/createUserStory', {
        user_story: description,
        proj_name: selectedProject,
        priority: priority
      })
      .then(response => {
        console.log('User story created successfully:', response.data);
      })
      .catch(error => {
        console.error('Error creating user story:', error);
      });
    };


<label>
          Manager:
          <select onChange={(e) => setManager(e.target.value)} value={mgr_id}>
            <option value="">Select Manager</option>
            {users.map((user, index) => {
             return <option key={index} value={user._id}>
                {user.f_Name} {user.l_Name}
              </option>
            })}
          </select>
        </label>
  return (
    <div>
      <h2>Create User Story</h2>
      <label>Select Project:</label>
      <select onChange={(e) => setSelectedProject(e.target.value)} value={selectedProject}>
        <option value="">Select Project</option>
        {projects.map(project => (
          <option key={project._id} value={project._id}>{project.proj_name}</option>
        ))}
      </select>
      <label>User Story Description:</label>
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
      <label>User Story Priority:</label>
            <input type="number" value={priority} onChange={(e) => setPriority(e.target.value)} />
      <button onClick={handleSubmit}>Submit User Story</button>
    </div>
  );
}

export default CreateUserStory;
