import React from 'react';
import { Link } from 'react-router-dom';

//Here Mybutton is my welcom and prompts the login or sign up 
function MyButton() {

    const buttonContainerStyle = {
      textAlign: 'center',
      color: 'blue', 
      marginTop: '50px', 
    };

    const buttonStyle = {
      color: 'black',
      margin: '10px',
    };

    const loggedInUser = localStorage.getItem('loggedInUser')

    const handleSignOut = (event) => {
      event.preventDefault()
      localStorage.clear()
      navigate("/Login");
  }

    return (
      <div style={buttonContainerStyle}>

        <h1>Welcome There </h1>
        <p>Please Select One:</p>

        <Link to="/login">
          <button style={buttonStyle}>Login</button>
        </Link>
        <Link to="/signup">
          <button style={buttonStyle}>Sign Up</button>
        </Link>

        <Link to="/createTeam">
          <button style={buttonStyle}>Create Your Team</button>
        </Link>
        <Link to="/Viewpage">
          <button style={buttonStyle}>Veiw All Teams</button>
        </Link>
        <Link to="/createProject">
          <button style={buttonStyle}>Create A Project</button>
        </Link>
        <Link to="/viewAllProjects">
          <button style={buttonStyle}>Veiw All Projects</button>
        </Link>
        <Link to="/createTeamRoster">
          <button style={buttonStyle}>Create Team Roster</button>
        </Link>
        <Link to="/Userstory">
          <button style={buttonStyle}>Create User Story</button>
        </Link>
        <>
            { loggedInUser != null &&
                   <p> {"Welcome! " + loggedInUser}</p>
                }
                </>

              <>
                { loggedInUser != null &&
                    <button type="button" onClick={(event) => {
                    handleSignOut(event)
                }}>Sign Out</button>
                }
                </>
                <>
                { loggedInUser == null &&
                    <p className="text-center">
                      Already have an account? <Link to="/login">Login</Link>
                    </p>
                }
                </>

      </div>
    );
  }
  export default MyButton;
