import React from 'react';
import { Link, navigate } from '@reach/router';
import axios from 'axios'; // Import Axios


function WelcomeUser() {

    const navigate = useNavigate();
    const loggedInUser = localStorage.getItem('loggedInUser');

  const handleSignOut = (event) => {
    event.preventDefault();
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div>

//wecolmoe should have the user first and last name
<h1>Welcome There </h1>
        {loggedInUser && <p>Welcome, {loggedInUser}</p>}
        <p>Please Select One:</p>
        <Link to="/login">
          <button style={buttonStyle}>Login</button>
        </Link>
        <Link to="/signup">
          <button style={buttonStyle}>Sign Up</button>
        </Link>
        <Link to="/createTeam">
          <button style={buttonStyle}>Create Your Team</button>
        </Link>
        <Link to="/Viewpage">
          <button style={buttonStyle}>Veiw All Teams</button>
        </Link>
        <Link to="/createProject">
          <button style={buttonStyle}>Create A Project</button>
        </Link>
        <Link to="/viewAllProjects">
          <button style={buttonStyle}>Veiw All Projects</button>
        </Link>
        <Link to="/createTeamRoster">
          <button style={buttonStyle}>Create A Team Roster</button>
        </Link>
        <Link to="/UserStory">
          <button style={buttonStyle}>Create User Story</button>
        </Link>

      {loggedInUser != null && (
        <>
          <p>{"Welcome! " + loggedInUser}</p>
          <button type="button" onClick={handleSignOut}>Sign Out</button>
        </>
      )}

      {loggedInUser == null && (
        <p className="text-center">
          Already have an account? <Link to="/login">Login</Link>
        </p>
      )}
    </div>
  );
}

export default WelcomeUser;
