import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CreateEvent() {
  const [email, setEmail] = useState('');
  const [eventName, setEventName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [budget, setBudget] = useState('');
  const [bio, setBio] = useState('');
  const [friendsEmails, setFriendsEmails] = useState('');

  const loggedInUser = localStorage.getItem('loggedInUser');
  const navigate = useNavigate();

  // Set the email field with the logged-in user's email
  const [userEmail, setUserEmail] = useState(loggedInUser.split(' ')[2]);

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = {
      email: userEmail,
      eventName,
      startDate,
      endDate,
      budget,
      bio,
      friendsEmails: friendsEmails.split(',').map(email => email.trim()),
    };

    axios.post('http://localhost:9000/createEvent', formData)
      .then(response => {
        console.log(response);
        navigate('/welcome');
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <form style={{ width: '300px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', backgroundColor: 'white' }}
      onSubmit={handleSubmit}>
      <div style={{ marginBottom: '10px' }}>
        <label>Email:</label>
        <input type="email" value={userEmail} readOnly/>
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Event Name:</label>
        <input type="text" placeholder="Enter event name" value={eventName} onChange={(event) => setEventName(event.target.value)} />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Start Date:</label>
        <input type="date" value={startDate} onChange={(event) => setStartDate(event.target.value)} />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>End Date:</label>
        <input type="date" value={endDate} onChange={(event) => setEndDate(event.target.value)} />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Budget:</label>
        <span>$</span>
        <input type="number" placeholder="Enter budget" value={budget} onChange={(event) => setBudget(event.target.value)} />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Bio:</label>
        <textarea placeholder="Enter event bio" rows="4" value={bio} onChange={(event) => setBio(event.target.value)}></textarea>
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Add Friends:</label>
        <textarea placeholder="Enter friend's emails (separated by commas)" rows="2" value={friendsEmails} onChange={(event) => setFriendsEmails(event.target.value)}></textarea>
      </div>
      <button type="submit">Create Event</button>
    </form>
  );
}

export default CreateEvent;
