import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; 

function ViewEvent() {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:9000/getEvent')
      .then(response => {
        console.log('Response Data:', response.data); 
        setEvents(response.data);
      })
      .catch(error => {
        console.error('Error fetching Event:', error);
        setError(error.message); // Store the error message in state
      });
  }, []);

  if (error) {
    return <div>Error: {error}</div>; // Render the error message
  }

  return (
    <div>
      <h2>View Event</h2>
      <ul>
        {events.map(event => (
          <li key={event._id}>
            <h3>Event Name: {event.eventName}</h3>
            <p>Start Date: {event.startDate}</p>
            <p>End Date: {event.endDate}</p>
            <p>Budget: ${event.budget}</p>
            <p>Bio: {event.bio}</p>
            <p>Friends' Emails: {event.friendsEmails.join(', ')}</p>
          </li>
        ))}
      </ul>

      <Link to="/">
        <button>Go back</button>
      </Link>
    </div>
  );
}

export default ViewEvent;
