import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; 


/*
Create User Story: Create a page to add user stories to a project.
• Fetch a list of all created projects.
• Pick a project from a list.
• Add a descrip4on of the user story.
• Assign a priority to the user story (default:0)
*/
function CreateUserStory() {
  const [projects, setProjects] = useState([]);
  const [proj_name, setSelectedProject] = useState('');
  const [user_story, setDescription] = useState('');
  const [priority, setPriority] = useState(0);



  useEffect(() => {
    // Fetch list of all created projects
    axios.get('http://localhost:9000/getProjects')
      .then(function (response) {
        setProjects(response.data);
      })
      .catch(error => {
        console.error('Error creating user story', error);
      });
  }, []);

  // Function to handle submitting the user story
  const handleSubmit = (event) => {
    event.preventDefault()
    axios.post('http://localhost:9000/UserStory', {
      proj_name: proj_name,
      user_story: user_story,
      priority: priority
      })
      .then(response => {
        console.log('User story created successfully:', response.data);
        setSelectedProject('');
        setDescription('');
        setPriority(0);

      })
      .catch(error => {
        console.error('Error creating user story:', error);
      });
    };



  return (
    <div>
      <h2>Create User Story</h2>
      <form onSubmit={handleSubmit}>
        <label>Select Project:</label>
        <select value={proj_name} onChange={(e) => setSelectedProject(e.target.value)}>
          <option value="">Select Project</option>
          {projects.map(project => (
            <option key={project._id} value={project.setSelectedProject}>{project.setSelectedProject}</option>
          ))}
        </select>
        <label>User Story Description:</label>
        <textarea value={user_story} onChange={(e) => setDescription(e.target.value)} />
        <label>User Story Priority:</label>
        <input type="number" value={priority} onChange={(e) => setPriority(e.target.value)} />
        <button type="submit">Submit User Story</button>
      </form>
      <Link to="/">
        <button>Go back</button>
      </Link>
    </div>
  );

}

export default CreateUserStory;
