import React, { useState, useEffect } from 'react';
import axios from 'axios';
const Project = require('../../server/ProjectSchema.js')

const Createproj = () => {
  const [proj_name, setProjectName] = useState('');
  const [proj_desc, setProjectDescription] = useState('');
  const [prod_owner_id, setProductOwner] = useState('');
  const [mgr_id, setManager] = useState('');
  const [team_id, setTeam] = useState('');
  const [users, setUsers] = useState([]);

  const handleCreateProject = (event, proj_name, proj_desc, prod_owner_id, mgr_id, team_id) => {
    event.preventDefault()
    axios.post('http://localhost:9000/createProject', { proj_name, proj_desc, prod_owner_id, mgr_id, team_id })
        .catch((err) => alert('Error in Creating project'))
}

useEffect(() => {
  axios.get('http://localhost:9000/getUsers')
  .then(function (response) {
    setUsers(response.data)
  })
  .catch(function (error) {
    console.log(error);
  })
  }, []);


  return (
    <div>
      <h2>Create Project</h2>
      <form onSubmit={handleCreateProject}>
        <label>
          Project Name:
          <input type="text" value={proj_name} onChange={(e) => setProjectName(e.target.value)} />
        </label>
        <br />
        <label>
          Project Description:
          <input type="text" value={proj_desc} onChange={(e) => setProjectDescription(e.target.value)} />
        </label>
        <br />
        <label>
          Product Owner ID:
          <input type="text" value={prod_owner_id} onChange={(e) => setProductOwner(e.target.value)} />
        </label>
        <br />
        <label>
          Manager ID:
          <input type="text" value={mgr_id} onChange={(e) => setManager(e.target.value)} />
        </label>
        <br />
        <label>
          Team ID:
          <input type="text" value={team_id} onChange={(e) => setTeam(e.target.value)} />
        </label>
        <br />
        <button type="submit">Create Project</button>
      </form>
    </div>
  );
}


export default Createproj;
