  axios.get('http://localhost:9000/getUsers')
  .then(function (response) {
    setUsers(response.data)
  })
  .catch(function (error) {
    console.log(error);
  });

  // Fetch teams
  axios.get('http://localhost:9000/getTeam')
  .then(function (response) {
    setTeam(response.data);
  })
  .catch(function (error) {
    console.log(error);
  });
