import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CreateEvent() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    eventName: '',
    startDate: '',
    endDate: '',
    budget: '',
    bio: '',
    friendsEmails: '',
  });

  const navigate =useNavigate();

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:9000/createEvent', formData)
      .then(response => {
        console.log(response);
        navigate('/welcome');
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <form style={{ width: '300px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', backgroundColor: 'white' }}
    onSubmit={handleSubmit}>
      <div>
        <label htmlFor="firstName">First Name:</label>
        <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="lastName">Last Name:</label>
        <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="eventName">Event Name:</label>
        <input type="text" name="eventName" value={formData.eventName} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="startDate">Start Date:</label>
        <input type="date" name="startDate" value={formData.startDate} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="endDate">End Date:</label>
        <input type="date" name="endDate" value={formData.endDate} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="budget">Budget:</label>
        <input type="number" name="budget" value={formData.budget} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="bio">Bio:</label>
        <textarea name="bio" value={formData.bio} onChange={handleChange}></textarea>
      </div>
      <div>
        <label htmlFor="friendsEmails">Friends' Emails:</label>
        <textarea name="friendsEmails" value={formData.friendsEmails} onChange={handleChange}></textarea>
      </div>
      <button type="submit">Create Event</button>
    </form>
  );
}

export default CreateEvent;
