        event.preventDefault();
        axios.get('http://localhost:9000/login', { params: { teamName, setTeamName } })
          .then((res) => {
            if (res.data) {
              alert('Team creation successful');
              window.location.href = '/';

            } else {
              alert('Error creating team, Please Try Again');
            }
          })
          .catch((err) => alert('Error in Login'));

      }
