import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function CreateTeam() {
  const [teamName, setTeamName] = useState('');

 const NameContainerStyle = {
        textAlign: 'center',
        color: 'purple ',
        margin: '10px',
      };


    const handleSubmit = (event) => {
        event.preventDefault();
        axios.get('http://localhost:9000/createTeam', {  team_name  })
        //axios.get('http://localhost:9000/createTeam', {  team_name: teamName  })

        .then((res) => {
            if (res.data) {
              alert('Team creation successful');
              window.location.href = '/';

            } else {
              alert('Error creating team, Please Try Again');
            }
          })
          .catch((err) => alert('Error in Creating a Team'));

      }

      return (
        <div style={NameContainerStyle}>
          <h1>Create Team Page</h1>
            <label>
              Team Name:
              <input id="Team Name" value={teamName} onChange={(e) => setTeamName(e.target.value)} />
            </label>
            <br />


          <Link to="/">Go back</Link>

        <Link to="/ThankYou">
          <button style={NameContainerStyle} onClick={handleSubmit}>Submit</button>
        </Link>
        </div>
      );
    }


export default CreateTeam;
