import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';

/*
Create Team Roster: Create a page to add members to a team.
• Fetch the list of created Teams.
• Pick a team from the list.
• Fetch a list of all users registered to the system.
• Select user(s) to add to a team.
*/
function CreateTeamRoster() {
  const [teams, setTeams] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState('');
  const [selectedUsers, setSelectedUsers] = useState([]);

  useEffect(()=>{
	axios.get('http://localhost:9000/getUsers').then((res)=>setUsers(res.data))
	axios.get('http://localhost:9000/getTeams').then((res)=>setTeams(res.data))
}, []);


  // Function to handle adding members to the team
  const handleAddMembers = (event) => {
    event.preventDefault();

    axios.post('http://localhost:9000/createTeamRoster', { teamros: selectedTeam, 
    memberid: selectedUsers.map(user => user.value)
   })
      .then(response => {
        console.log('Team roster created successfully:', response.data);

        setSelectedUsers([]);
      })
      .catch(error => {
        console.error('Error creating team roster:', error);
        // Show an error message to the user
        alert('Error creating team roster: ' + error.message);
      });
  };


  const userOptions = users.map((user) => {
    return {label: user.f_Name + user.l_Name, value: user._id}
    })

  return (
    <div>
      <h2>Create Team Roster</h2>
      <label>Select Team:</label>
      <select value={selectedTeam} onChange={(e) => setSelectedTeam(e.target.value)}>
        <option value="">Select Team</option>
        {teams.map((team) => (
          <option key={team._id} value={team._id}>{team.team_name}</option>
        ))}
      </select>
      <label>Select Members:</label>
      <Select
        isMulti
        value={selectedUsers}
        onChange={setSelectedUsers}
        options={userOptions}
      />
      <button onClick={handleAddMembers}>Add Members</button>
    </div>
  );
}

export default CreateTeamRoster;
