import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CreateEvent() {
  const [email, setEmail] = useState('');
  const [eventName, setEventName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [budget, setBudget] = useState('');
  const [bio, setBio] = useState('');
  const [friendsEmails, setFriendsEmails] = useState('');

  const loggedInUser = localStorage.getItem('loggedInUser');
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = {
      email,
      eventName,
      startDate,
      endDate,
      budget,
      bio,
      friendsEmails,
    };

    axios.post('http://localhost:9000/createEvent', formData)
      .then(response => {
        console.log(response);
        navigate('/welcome');
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <form style={{ width: '300px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', backgroundColor: 'white' }}
      onSubmit={handleSubmit}>

      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label htmlFor="eventName">Event Name:</label>
        <input type="text" name="eventName" value={eventName} onChange={(e) => setEventName(e.target.value)} />
      </div>
      <div>
        <label htmlFor="startDate">Start Date:</label>
        <input type="date" name="startDate" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
      </div>
      <div>
        <label htmlFor="endDate">End Date:</label>
        <input type="date" name="endDate" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
      </div>
      <div>
        <label htmlFor="budget">Budget:</label>
        <input type="number" name="budget" value={budget} onChange={(e) => setBudget(e.target.value)} />
      </div>
      <div>
        <label htmlFor="bio">Bio:</label>
        <textarea name="bio" value={bio} onChange={(e) => setBio(e.target.value)}></textarea>
      </div>
      <div>
        <label htmlFor="friendsEmails">Friends' Emails:</label>
        <textarea name="friendsEmails" value={friendsEmails} onChange={(e) => setFriendsEmails(e.target.value)}></textarea>
      </div>
      <button type="submit">Create Event</button>
    </form>
  );
}

export default CreateEvent;
