import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CreateEvent() {
  const [formData, setFormData] = useState({
    email: '',
    eventName: '',
    startDate: '',
    endDate: '',
    budget: '',
    bio: '',
    friendsEmails: ''
  });

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('http://localhost:9000/createEvent', formData)
      .then(response => {
        console.log(response);
        navigate('/welcome');
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <form style={{ width: '300px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', backgroundColor: 'white' }}
      onSubmit={handleSubmit}>
      <div style={{ marginBottom: '10px' }}>
        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Enter your email" />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Event Name:</label>
        <input type="text" name="eventName" value={formData.eventName} onChange={handleChange} placeholder="Enter event name" />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Start Date:</label>
        <input type="date" name="startDate" value={formData.startDate} onChange={handleChange} />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>End Date:</label>
        <input type="date" name="endDate" value={formData.endDate} onChange={handleChange} />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Budget:</label>
        <span>$</span>
        <input type="number" name="budget" value={formData.budget} onChange={handleChange} placeholder="Enter budget" />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Bio:</label>
        <textarea name="bio" value={formData.bio} onChange={handleChange} placeholder="Enter event bio" rows="4" />
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>Add Friends:</label>
        <textarea name="friendsEmails" value={formData.friendsEmails} onChange={handleChange} placeholder="Enter friend's emails (separated by commas)" rows="2" />
      </div>
      <button type="submit">Create Event</button>
    </form>
  );
}

export default CreateEvent;
