import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Menubar from './Menubar'; // Import the MenuBar component

function Welcomepage() {
    const [signUpInfo, setSignUpInfo] = useState({
        firstName: '',
        lastName: '',
        email: ''
    });
    const [eventInfo, setEventInfo] = useState({
        eventName: '',
        startDate: '',
        endDate: '',
        budget: '',
        bio: '',
        friendsEmails: ''
    });
    const [page, setPage] = useState(1); // 1 for sign up, 2 for event details

    const handleSignUpSubmit = (event) => {
        event.preventDefault();
        // Handle sign up form submission here (e.g., send sign up data to backend)
        // Move to the next page
        setPage(2);
    };

    const handleEventSubmit = (event) => {
        event.preventDefault();
        // Handle event details form submission here (e.g., send event data to backend)
        // For example, you can console log the data:
        console.log('Event Info:', eventInfo);
        // After handling event submission, you can redirect the user to another page if needed
        // navigate('/some-other-page');
    };

    const handleChange = (e, type) => {
        const { name, value } = e.target;
        if (type === 'signUp') {
            setSignUpInfo({ ...signUpInfo, [name]: value });
        } else {
            setEventInfo({ ...eventInfo, [name]: value });
        }
    };

    return (
        <div style={{ backgroundColor: 'lightblue', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', fontFamily: 'Arial' }}>
            <div style={{ position: 'absolute', top: '20px', left: '20px' }}>
                <Menubar />
            </div>
            <h1 style={{ textAlign: 'center', marginTop: '50px' }}>Welcome There</h1>
            {page === 1 ? (
                <div style={{ textAlign: 'center', marginTop: '50px' }}>
                    <h2>Sign Up</h2>
                    <form onSubmit={handleSignUpSubmit} style={{ width: '300px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', backgroundColor: 'white' }}>
                        <div style={{ marginBottom: '10px' }}>
                            <label>First Name:</label>
                            <input type="text" name="firstName" placeholder="Enter your first name" value={signUpInfo.firstName} onChange={(e) => handleChange(e, 'signUp')} />
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Last Name:</label>
                            <input type="text" name="lastName" placeholder="Enter your last name" value={signUpInfo.lastName} onChange={(e) => handleChange(e, 'signUp')} />
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Email:</label>
                            <input type="email" name="email" placeholder="Enter your email" value={signUpInfo.email} onChange={(e) => handleChange(e, 'signUp')} />
                        </div>
                        <button type="submit">Next</button>
                    </form>
                </div>
            ) : (
                <div style={{ textAlign: 'center', marginTop: '50px' }}>
                    <h2>Create Event</h2>
                    <form onSubmit={handleEventSubmit} style={{ width: '300px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', backgroundColor: 'white' }}>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Event Name:</label>
                            <input type="text" name="eventName" placeholder="Enter event name" value={eventInfo.eventName} onChange={(e) => handleChange(e, 'event')} />
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Start Date:</label>
                            <input type="date" name="startDate" value={eventInfo.startDate} onChange={(e) => handleChange(e, 'event')} />
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>End Date:</label>
                            <input type="date" name="endDate" value={eventInfo.endDate} onChange={(e) => handleChange(e, 'event')} />
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Budget:</label>
                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                <span>$</span>
                                <input type="number" name="budget" placeholder="Enter budget" value={eventInfo.budget} onChange={(e) => handleChange(e, 'event')} />
                            </div>
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Bio:</label>
                            <textarea name="bio" placeholder="Enter event bio" rows="4" value={eventInfo.bio} onChange={(e) => handleChange(e, 'event')} />
                        </div>
                        <div style={{ marginBottom: '10px' }}>
                            <label>Add Friends:</label>
                            <textarea name="friendsEmails" placeholder="Enter friend's emails (separated by commas)" rows="2" value={eventInfo.friendsEmails} onChange={(e) => handleChange(e, 'event')} />
                        </div>
                        <button type="submit">Create Event</button>
                    </form>
                </div>
            )}
            <p style={{ textAlign: 'center', marginTop: '20px', fontWeight: 'bold' }}>Here are the list of Holidays!</p>
            <p style={{ textAlign: 'center', marginTop: '20px' }}>
                New Year's Day
                Christmas Day
                Thanksgiving Day (in the United States)
                Easter Sunday
                Hanukkah (Jewish holiday)
                Diwali (Hindu holiday)
                Ramadan (Islamic holiday)
                Father's Day
                International Women's Day
                International Workers' Day (May Day)
                Holi (Hindu spring festival of colors)
                Bastille Day (French National Day)
                Eid al-Fitr (Islamic holiday marking the end of Ramadan)
                Eid al-Adha
                Chinese New Year (Lunar New Year)
                Independence Day (in various countries)
                Valentine's Day
                Halloween
                Saint Patrick's Day
                Mother's Day
            </p>
        </div>
    );
}

export default Welcomepage;
