import { useState } from 'react';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';


/*
I want it to start at be the beginning, then click the botton 
it takes me to login or sign up, for the sign up one the user 
input there user id and password and submit it and then it 
takes me to thank you for loging in, but for the signing up 
page as for first name last name user id and password, then 
they submit and takes them to a thank you for signing up and 
a back to the begining button or go back to login instead of 
the main page
*/

//Here Mybutton is my welcom and prompts the login or sign up 
function MyButton() {

  const buttonContainerStyle = {
    textAlign: 'center',
    color: 'blue', 
    marginTop: '50px', 
  };

  const buttonStyle = {
    color: 'pink',
    margin: '10px',
  };

  return (
    <div style={buttonContainerStyle}>

      <h1>Welcome There </h1>
      <p>Please Select One:</p>
      <Link to="/login">
        <button style={buttonStyle}>Login</button>
      </Link>
      <Link to="/signup">
        <button style={buttonStyle}>Sign Up</button>
      </Link>
    </div>
  );
}

/*
I cosumized my text made it pretty, and this needed to take in the input
the user inputs so need a handeler, space for user and password
and a submit button which links to the thank you back which i will descibe more
*/
function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const loginContainerStyle = {
    textAlign: 'low center',
    color: 'blue', 
    marginTop: '50px', 
  };

  const loginStyle = {
    color: 'green',
    margin: '10px',
  };

  const header={
    color: 'black',
    textAlign: 'center',
  };

  const handleLogin = (event) => {
    event.preventDefault();
    axios.get('http://localhost:3000/getUser', { params: { username, password } })
      .then((res) => {
        if (res.data) {
          alert('Login Successful');
          // Redirect or perform other actions after successful login
        } else {
          alert('Wrong Credentials');
        }
      })
      .catch((err) => alert('Error in Login'));

  }


  return (
    <div style={loginContainerStyle}>
      <h1 style={header}>Login Page</h1>
      <label>
        User name:
        <input id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <label>
        Password:
        <input id="password" name="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <Link to="/ThankYou">
        <button style={loginStyle} onClick={handleLogin}>Submit</button>
      </Link>
    </div>
  );
}



/*
For the Signup page, this also needed to take in the input
the user inputs so need a handeler, space for first and last name 
user and password and a submit button and submit button to the thank you page
*/
function SignUp() {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const signupContainerStyle = {
    textAlign: 'center',
    color: 'blue', 
    marginTop: '50px', 
  };

  const signupStyle = {
    color: 'green',
    margin: '50px',
  };

  const header={
    color: 'pink',
    textAlign: 'center',
  };

  const handleSignUp = (event) => {
    event.preventDefault();
    axios.post('http://localhost:9000/createUser', { firstName, lastName, username, password })
      .then((res) => {
        alert('Signup Successful');
        // Redirect or perform other actions after successful signup
      })
      .catch((err) => alert('Error in Signing Up'));
  };

  return (
    <div style={signupContainerStyle}>
      <h1 style={header}>Sign Up</h1>
      <label>
        First name:
        <input id="firstName" name="firstName" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
      </label>
      <label>
        Last name:
        <input id="lastName" name="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} />
      </label>
      <label>
        User name:
        <input id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <label>
        Password:
        <input id="password" name="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <Link to="/ThankYou">
        <button  style= {signupStyle} onClick={handleSignUp}>Submit</button>
      </Link>
    </div>
  );
}

/*
The thank you page prompt the user to either go back to start or bcak to login
so say they just signed up they may go and log in now
*/
function ThankYou(){
  const thankyouContainerStyle = {
    textAlign: 'center',
    color: 'purple', 
    marginTop: '50px', 
  };

  const thankyouStyle = {
    color: 'green',
    margin: '50px',
  };


return (
  <div style= {thankyouContainerStyle}>
      <h1>Thank Your for your submition </h1>
      <p>Back to Start:</p>
      <Link to="/MyButton">
        <button sytle ={thankyouStyle} >Click Here</button>
      </Link>
      <p> </p>
      <p> OR</p>

      <Link to="/s">
        <button>Login Now</button>
      </Link>
  </div>
)
}


/*
This ties it all together like the routes for when the button is clicked it actually takes the
somewhere, wether the login page, signup back, thank you back or the welcome page
Made it so much easier to go to tthose pages
*/
export default function MyApp() {
  return (
    <Router>
      <div>

        <Routes>
          <Route path="/" element={<MyButton />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/ThankYou" element={<ThankYou />} />
          <Route path="/s" element={<Login />} />
          <Route path="/MyButton" element={<MyButton />} />


        </Routes>
      </div>
    </Router>
  );
}
